from functools import lru_cache

class Solution:
    def longestCommonSubsequence(self, text1: str, text2: str) -> int:
        # two inputs to dp: i1, and i2
        n1 = len(text1)
        n2 = len(text2)

        # @lru_cache(maxsize = None)
        # def dp(i1,i2):
        #     if i1 < 0 or i2 < 0:
        #         return 0
            
        #     if text1[i1] == text2[i2]:
        #         return dp(i1-1,i2-1) + 1
        #     else:
        #         return max(dp(i1-1,i2),dp(i1,i2-1))

        # return dp(n1-1,n2-1)

        dp = [[0]*(n2+1) for j in range(n1+1)]

        for i1 in range(n1):
            for i2 in range(n2):
                if text1[i1] == text2[i2]:
                    dp[i1+1][i2+1] = dp[i1][i2] + 1
                else:
                    dp[i1+1][i2+1] = max(dp[i1][i2+1],dp[i1+1][i2])

        return dp[n1][n2]

